    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create a task to extract effective tax rate information for each chunk
        task = "Extract any information related to the effective tax rate for FY2021 and FY2022."
        advice = "Focus on financial statements, notes, and management discussion sections."

        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task=task,
                advice=advice
            )
            job_manifests.append(job_manifest)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    # Initialize a dictionary to store outputs by task_id and chunk_id
    aggregated_outputs = {}

    for job in jobs:
        task_id = job.manifest.task_id
        chunk_id = job.manifest.chunk_id

        # Initialize a list for each task_id and chunk_id if not already present
        if (task_id, chunk_id) not in aggregated_outputs:
            aggregated_outputs[(task_id, chunk_id)] = []

        # Append the output to the corresponding list
        aggregated_outputs[(task_id, chunk_id)].append(job.output)

    # Prepare the final aggregated string
    final_output = []
    for (task_id, chunk_id), outputs in aggregated_outputs.items():
        for output in outputs:
            if output.answer:
                final_output.append(f"Chunk ID: {chunk_id}, Task ID: {task_id}\nAnswer: {output.answer}\n")
            if output.explanation:
                final_output.append(f"Explanation: {output.explanation}\n")
            if output.citation:
                final_output.append(f"Citation: {output.citation}\n")

    return "\n".join(final_output)
