    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)
        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task="Extract any financial details related to the separation of Kenvue, including cash proceeds or inflows.",
                advice="Focus on sections discussing divestitures, spin-offs, or financial transactions related to Kenvue."
            )
            job_manifests.append(job_manifest)
    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    relevant_outputs = []
    for job in jobs:
        output = job.output
        if output.answer:
            relevant_outputs.append(f"Answer: {output.answer}\nExplanation: {output.explanation}\nCitation: {output.citation}")

    aggregated_output = "\n\n".join(relevant_outputs)
    return aggregated_output
