def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []
    task = "Extract the total cash dividends paid and net income attributable to shareholders for FY2022."
    advice = "Focus on financial figures related to cash dividends and net income for the fiscal year 2022."

    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)
        for chunk in chunks:
            job_manifests.append(JobManifest(chunk=chunk, task=task, advice=advice))

    return job_manifests


def transform_outputs(
    jobs: List[Job],
) -> str:
    dividends_paid = None
    net_income = None
    explanations = []
    citations = []

    for job in jobs:
        output = job.output
        if output.answer:
            if "dividends" in output.answer.lower():
                dividends_paid = output.answer
            elif "net income" in output.answer.lower():
                net_income = output.answer
        if output.explanation:
            explanations.append(output.explanation)
        if output.citation:
            citations.append(output.citation)

    aggregated_info = "Extracted Information:\n"
    if dividends_paid:
        aggregated_info += f"Total Cash Dividends Paid: {dividends_paid}\n"
    if net_income:
        aggregated_info += f"Net Income Attributable to Shareholders: {net_income}\n"

    aggregated_info += "\nExplanations:\n" + "\n".join(explanations)
    aggregated_info += "\n\nCitations:\n" + "\n".join(citations)

    return aggregated_info
