def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it by sections
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            # Create a job to extract total current assets
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total current assets for FY2016 from this chunk.",
                advice="Look for sections labeled as balance sheet or current assets.",
                task_id=1
            ))

            # Create a job to extract total current liabilities
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total current liabilities for FY2016 from this chunk.",
                advice="Look for sections labeled as balance sheet or current liabilities.",
                task_id=2
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    current_assets = []
    current_liabilities = []

    for job in jobs:
        if job.manifest.task_id == 1 and job.output.answer:
            current_assets.append((job.output.answer, job.output.citation))
        elif job.manifest.task_id == 2 and job.output.answer:
            current_liabilities.append((job.output.answer, job.output.citation))

    # Aggregate the results
    aggregated_result = "Extracted Data:\n"

    if current_assets:
        aggregated_result += "Total Current Assets:\n"
        for asset, citation in current_assets:
            aggregated_result += f"Value: {asset}, Source: {citation}\n"

    if current_liabilities:
        aggregated_result += "Total Current Liabilities:\n"
        for liability, citation in current_liabilities:
            aggregated_result += f"Value: {liability}, Source: {citation}\n"

    return aggregated_result
