    task_id_liabilities = 2  # Unique identifier for extracting current liabilities

    # Chunk the document by sections
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            # Create a task for extracting total current assets
            task_assets = "Extract the total current assets for FY2016 from this chunk."
            job_manifest_assets = JobManifest(
                chunk=chunk,
                task=task_assets,
                advice="Look for sections labeled 'Current Assets' or similar for FY2016."
            )
            job_manifests.append(job_manifest_assets)

            # Create a task for extracting total current liabilities
            task_liabilities = "Extract the total current liabilities for FY2016 from this chunk."
            job_manifest_liabilities = JobManifest(
                chunk=chunk,
                task=task_liabilities,
                advice="Look for sections labeled 'Current Liabilities' or similar for FY2016."
            )
            job_manifests.append(job_manifest_liabilities)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    def filter_fn(job):
        return job.output.answer is not None and job.output.answer.strip() != ""

    # Filter jobs
    for job in jobs:
        job.include = filter_fn(job)

    # Aggregate and filter jobs
    tasks = {}
    for job in jobs:
        task_id = job.manifest.task_id
        chunk_id = job.manifest.chunk_id

        if task_id not in tasks:
            tasks[task_id] = {
                "task_id": task_id,
                "task": job.manifest.task,
                "chunks": {},
            }

        if chunk_id not in tasks[task_id]["chunks"]:
            tasks[task_id]["chunks"][chunk_id] = []

        tasks[task_id]["chunks"][chunk_id].append(job)

    # Build the aggregated string
    aggregated_str = ""
    for task_id, task_info in tasks.items():
        aggregated_str += f"## Task (task_id=`{task_id}`): {task_info['task']}\n\n"

        for chunk_id, chunk_jobs in task_info["chunks"].items():
            filtered_jobs = [j for j in chunk_jobs if j.include]

            aggregated_str += f"### Chunk # {chunk_id}\n"
            if filtered_jobs:
                for idx, job in enumerate(filtered_jobs, start=1):
                    aggregated_str += f"   -- Job {idx} (job_id=`{job.manifest.job_id}`):\n"
                    aggregated_str += f"   Answer: {job.output.answer}\n"
                    aggregated_str += f"   Explanation: {job.output.explanation}\n"
                    if job.output.citation:
                        aggregated_str += f"   Citation: {job.output.citation}\n"
                    aggregated_str += "\n"
            else:
                aggregated_str += "   No jobs returned successfully for this chunk.\n"

        aggregated_str += "\n-----------------------\n"

    return aggregated_str
