def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it into smaller sections
    # for detailed information extraction.
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        # Create a task to extract total stock repurchase amount for FY 2023
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total amount spent on stock repurchases for FY 2023.",
                advice="Look for any financial summaries or tables that mention stock repurchases for the entire fiscal year.",
                task_id=1
            ))

        # Create a task to extract stock repurchase amount for Q4 of FY 2023
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the amount spent on stock repurchases in Q4 of FY 2023.",
                advice="Focus on sections detailing quarterly financial activities, especially Q4.",
                task_id=2
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    total_fy2023_repurchase = None
    q4_fy2023_repurchase = None

    # Aggregate outputs by task_id
    for job in jobs:
        output = job.output
        if job.manifest.task_id == 1 and output.answer:
            # Extract total FY 2023 repurchase amount
            if total_fy2023_repurchase is None:
                total_fy2023_repurchase = output.answer
        elif job.manifest.task_id == 2 and output.answer:
            # Extract Q4 FY 2023 repurchase amount
            if q4_fy2023_repurchase is None:
                q4_fy2023_repurchase = output.answer

    # Prepare the aggregated string for further analysis
    result = "Total FY 2023 Stock Repurchase: {}\nQ4 FY 2023 Stock Repurchase: {}\n".format(
        total_fy2023_repurchase, q4_fy2023_repurchase
    )

    return result
