def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        # Create tasks to extract cash from operations from the cash flow statement
        for chunk in chunks:
            if "cash flow statement" in chunk.lower():
                job_manifests.append(JobManifest(
                    chunk=chunk,
                    task="Extract the cash from operations for FY2015.",
                    advice="Look for sections related to 'Cash Flows from Operating Activities'.",
                    task_id=1
                ))

        # Create tasks to extract total current liabilities from the statement of financial position
        for chunk in chunks:
            if "statement of financial position" in chunk.lower() or "balance sheet" in chunk.lower():
                job_manifests.append(JobManifest(
                    chunk=chunk,
                    task="Extract the total current liabilities for FY2015.",
                    advice="Look for sections listing liabilities, especially those due within one year.",
                    task_id=2
                ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    cash_from_operations = None
    total_current_liabilities = None

    # Aggregate outputs by task_id
    for job in jobs:
        if job.manifest.task_id == 1:
            if job.output.answer:
                cash_from_operations = job.output.answer
        elif job.manifest.task_id == 2:
            if job.output.answer:
                total_current_liabilities = job.output.answer

    # Construct the aggregated string
    result = "Aggregated Financial Data for FY2015:\n"
    if cash_from_operations:
        result += f"Cash from Operations: {cash_from_operations}\n"
    else:
        result += "Cash from Operations: Not found\n"

    if total_current_liabilities:
        result += f"Total Current Liabilities: {total_current_liabilities}\n"
    else:
        result += "Total Current Liabilities: Not found\n"

    return result
