    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)
        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task=task_instruction,
                advice="Focus on the income statement section and look for net income figures specifically for FY2019.",
            )
            job_manifests.append(job_manifest)

    return job_manifests


def transform_outputs(
    jobs: List[Job],
) -> str:
    net_income_results = []

    for job in jobs:
        output = job.output
        if output.answer:
            net_income_results.append(f"Chunk ID {job.manifest.chunk_id}: {output.answer} (Citation: {output.citation})")

    aggregated_result = "\n".join(net_income_results)
    return f"Aggregated Net Income Results:\n{aggregated_result}"
